INSERT INTO "cookies" ("nombre","proveedor","enlace","duracion","info","prioridad","info_idioma","proveedor_idioma","idioma","tipo","id","connectionType") VALUES ('AWSALB','Amazon','','7 egun','Cookie hau saio osoan zehar itsaskortasuna sartzeko erabiltzen da. Saioa instantziari esleitzeko erabiltzen da. Karga-balantzaileak (zerbitzariak) bezero baten eskaera bat jasotzen duenean lehen aldiz, eskaera helmuga batera bideratzen du, aukeratutako helmugako informazioa kodetzen duen cookie enkriptatu hori sortuz, eta bezeroari emandako erantzunean sartzen du. Gainera, bisitariari zein zerbitzari-talde ari zaion zerbitzatzen erregistratzen du, eta karga orekatzeko testuinguruan erabiltzen da erabiltzailearen esperientzia optimizatzeko.','',NULL,NULL,'eu','0',NULL,'HTTP');
